import React, { useState, useEffect } from "react";
import "./HomePage.css";
import Table from "react-bootstrap/Table";
import { observer } from "mobx-react";
import axios from 'axios';
import { getPhotos, deletePhoto, APIURL, getVotes } from "./requests";
import $ from "jquery";
class HomePage extends React.Component {

    constructor() {
        super()
        this.state = {
            photos: [],
            id: '',
            sub_id: '',
            votes: '',
            vote_up: '',
            vote_down: ''
        }
    }



    componentDidMount() {

        const getAllPhotos = async () => {
            const response = await getPhotos();
            this.setState({
                photos: response.data
            })
        };

        const getAllVotes = async () => {
            const votes = await getVotes()

            const positive = votes.data.filter((vote) => {
                return vote.value == 1
            })

            const negative = votes.data.filter((vote) => {
                return vote.value == 0
            })

            this.setState({
                vote_up: positive.length,
                vote_down: negative.length
            })

            console.log('vot1', positive.length, 'vot2', negative.length)
            this.setState({
                votes: votes.data
            })
        };
        getAllPhotos()
        getAllVotes()
    };

    handleFavSubmit = (event, fav) => {
        event.preventDefault();

        axios.post(`https://api.thecatapi.com/v1/favourites`, fav, { headers: { "Content-Type": "application/json", "x-api-key": "52bd8cc7-7b96-4219-8015-d7c56f3eaa9f" } }
        )
            .then(res => {
                console.log('fpf', res);
                console.log(res.data);
            })
    }

    handleUnFavSubmit = (event, unfav) => {
        event.preventDefault();

        axios.post(`https://api.thecatapi.com/v1/favourites/${unfav}`, unfav, { headers: { "Content-Type": "application/json", "x-api-key": "52bd8cc7-7b96-4219-8015-d7c56f3eaa9f" } }
        )
            .then(res => {
                console.log('fpf', res);
                console.log(res.data);
            })
    }

    handleVoteSubmit = (event, voteUp) => {
        event.preventDefault();

        axios.post(`https://api.thecatapi.com/v1/votes`, voteUp, { headers: { "Content-Type": "application/json", "x-api-key": "52bd8cc7-7b96-4219-8015-d7c56f3eaa9f" } }
        )
            .then(res => {
                console.log('fpf', res);
                console.log(res.data);
            })
    }

    render() {

        console.log('rail', this.state.photos)
        const photoList = this.state.photos.map(p => {
            const fav = {
                image_id: p.id,
                sub_id: p.sub_id
            }

            const unfavourite = {
                image_id: p.id,
            }

            const voteUp = {
                image_id: p.id,
                sub_id: p.sub_id,
                value: true
            }

            const voteDown = {
                image_id: p.id,
                sub_id: p.sub_id,
                value: false
            }

            return (
                <div className="container" style={{position: 'absolute'}}>
                    <img key={p.id} src={p.url} className="img-fluid" style={{position: 'absolute'}} />
                    <div className="fave-select" style={{position: 'relative', left: '10px'}}>
                    <button type="button" className="btn btn-primary btn-lg btn-block" style={{position: 'relative'}} onClick={(e) => this.handleFavSubmit(e, fav)}>Favourite </button>
                    <button type="button" className="btn btn-secondary btn-lg btn-block" style={{position: 'relative'}} onClick={(e) => this.handleUnFavSubmit(e, unfavourite)}>Unfavourite</button>
                    </div>

                    <div className="vote-select">
                    <button type="button" className="btn btn-success btn-lg" style={{position: 'relative'}} onClick={(e) => this.handleVoteSubmit(e, voteUp)}>LOVE IT </button>
                    <button type="button" className="btn btn-danger btn-lg" style={{position: 'relative'}} onClick={(e) => this.handleVoteSubmit(e, voteDown)}>NOPE IT </button>
                    </div>
                    <p style={{position: 'absolute'}}>{this.state.vote_ups - this.state.vote_down} </p>
                </div>
            )
        })

        return (
            <div className="container">
                {photoList}
            </div>
        );

    }

}
export default observer(HomePage);
