import React from 'react'
const axios = require("axios");

class CatPage extends React.Component {

    constructor(props) {
        super(props);
        this.state ={
            sub_id: '',
            file: null
        };
        this.onFormSubmit = this.onFormSubmit.bind(this);
        this.onChange = this.onChange.bind(this);
    }
    onFormSubmit(e){
        e.preventDefault();
        const formData = new FormData();
        formData.append('myImage',this.state.file);
        formData.append('sub_id',this.state.sub_id);

        const config = {
            headers: { 
              "Content-Type": "multipart/form-data",
              "x-api-key": "52bd8cc7-7b96-4219-8015-d7c56f3eaa9f"
            }
        };
        axios.post("https://api.thecatapi.com/v1/images/upload",formData,config)
            .then((response) => {
                alert("The file is successfully uploaded");
            }).catch((error) => {
        });
    }
    onChange(e) {
        this.setState({sub_id: e.target.value, file:e.target.files[0]});
    }


    render() {
        return (
            <form onSubmit={this.onFormSubmit}>
                <h1>File Upload</h1>
                <input type="text" name="sub_id" onChange= {this.onChange} />
                <input type="file" name="file" onChange= {this.onChange} />
                <button className="btn btn-primary" type="submit">Upload</button>
            </form>
        )
    }
}

export default CatPage
