import React from 'react'
import history from '../src/history'

const axios = require("axios");

class CatPage extends React.Component {


  constructor(props) {
    super(props);
    this.state = {
      sub_id: '',
      file: '',
      status: null
    };
    this.onFormSubmit = this.onFormSubmit.bind(this);
    this.onChange = this.onChange.bind(this);
  }

  onFormSubmit(e) {
    e.preventDefault();
    const formData = new FormData();
    formData.append('sub_id', this.state.sub_id);
    formData.append('file', this.state.file);
    const config = {
      headers: { "Content-Type": "multipart/form-data", "x-api-key": "52bd8cc7-7b96-4219-8015-d7c56f3eaa9f" }
    };
    axios.post("https://api.thecatapi.com/v1/images/upload", formData, config)
      .then((response) => {
        alert("The file is successfully uploaded");
        this.setState({
          status: 'success'
        })
        if(response) {
          history.push('/')
        }
      }).catch((error) => {
        console.log('error', error)
      });
  }
  onChange(e) {
    this.setState({ file: e.target.files[0] });
  }

  render() {
    return (
      <div className="container">
        <form onSubmit={this.onFormSubmit} style={{textAlign: 'left'}}>
        <h1>Cat Api</h1>
          <div className="form-group">
            <label> Sub Id </label>
            <input type="sub_id" className="form-control" value={this.state.sub_id} onChange={(e) => this.setState({ sub_id: e.target.value })} />
          </div>
          <div className="form-group">
            <label> Image </label>
            <input type="file" className="form-control btn" value={this.state.file} name="file" onChange={this.onChange} />
          </div>
          <button type="submit" className="btn btn-primary">Submit</button>
        </form>
      </div>
    )
  }
}

export default CatPage
