import React, { useState, useEffect } from "react";
import "./HomePage.css";
import Table from "react-bootstrap/Table";
import { observer } from "mobx-react";
import axios from 'axios';
import { getPhotos, deletePhoto, APIURL, unfavourite } from "./requests";
class HomePage extends React.Component {

    constructor() {
        super()
        this.state = {
            photos: [],
            id: '',
            sub_id: ''
        }
    }



    componentDidMount() {

        const getAllPhotos = async () => {
            const response = await getPhotos();
            this.setState({
                photos: response.data
            })
          };

        getAllPhotos()
    };

    handleFavSubmit = (event, user) => {
        event.preventDefault();

        axios.post(`https://api.thecatapi.com/v1/favourites`, user, { headers: { "Content-Type": "application/json", "x-api-key": "52bd8cc7-7b96-4219-8015-d7c56f3eaa9f" }}
        )
          .then(res => {
            console.log('fpf', res);
            console.log(res.data);
          })
      }    

    handleUnFavSubmit = (event, user) => {
        event.preventDefault();

        axios.post(`https://api.thecatapi.com/v1/unfavourites`, user, { headers: { "Content-Type": "application/json", "x-api-key": "52bd8cc7-7b96-4219-8015-d7c56f3eaa9f" }}
        )
          .then(res => {
            console.log('fpf', res);
            console.log(res.data);
          })
    }    

    render() {

        console.log('rail', this.state.photos)
        const photoList = this.state.photos.map(p => {
            const user = {
                image_id: p.id,
                sub_id: p.sub_id
            }
            return (
                <div className="container">
                    <button type="button" className="btn btn-default" onClick={(e) => this.handleFavSubmit(e, user)}>Favourite <svg width="1em" height="1em" viewBox="0 0 16 16" className="bi bi-heart" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
  <path fill-rule="evenodd" d="M8 2.748l-.717-.737C5.6.281 2.514.878 1.4 3.053c-.523 1.023-.641 2.5.314 4.385.92 1.815 2.834 3.989 6.286 6.357 3.452-2.368 5.365-4.542 6.286-6.357.955-1.886.838-3.362.314-4.385C13.486.878 10.4.28 8.717 2.01L8 2.748zM8 15C-7.333 4.868 3.279-3.04 7.824 1.143c.06.055.119.112.176.171a3.12 3.12 0 0 1 .176-.17C12.72-3.042 23.333 4.867 8 15z"/>
</svg></button>
                    <button type="button" className="btn btn-primary" onClick={(e) => handleUnFavSubmit(e, user)}>Unfavourite</button>
                    <img key={p.id} src={p.url} className="img-fluid"/>
                </div>
            )
        })

        return (
            <div className="container">
                {photoList}
            </div>
        );

    }

}
export default observer(HomePage);
